#!/usr/bin/env python
# coding:utf-8

from time import perf_counter

def count(f):
    def time(*args,**kwargs):
        args = iter(args)
        s = 'f('
        for i in f.__code__.co_varnames:
            try:
                s += i+'='+str(next(args))+','
            except:
                for i,j in kwargs.items():
                    s += i+'='+str(j)
        start = perf_counter()
        exec(s + ')')
        print(f'函数:{f.__name__}  用时:{perf_counter()-start}秒')
        
    return time
